sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	// Add your code here
	var gr = new GlideRecord('x_851032_leave_man_leave_calculator');
	gr.addQuery("country.name",current.location.country);
	gr.query();
	if(gr.next()){
		if(gr.leaves_assignment == "y"){
			// calculate the Leaves the employee deserve
			//Current month not include
			var gdt = GlideDateTime();
			var currentMonth = gdt.getMonthLocalTime();
			var monthsLetf = 12 - currentMonth;
			var perMonth = gr.leaves / 12;
			var totalLeaves = monthsLetf * perMonth;

			// Insert the the record in the Leave Bucket table
			var leaveBucket = new GlideRecord('x_851032_leave_man_leave_bucket');
			leaveBucket.initialize();
			leaveBucket.leave_type = gr.leave_type;
			leaveBucket.accrued = totalLeaves;
			leaveBucket.balance = totalLeaves;
			leaveBucket.taken = 0;
			leaveBucket.employee = current.sys_id;
			leaveBucket.insert();

		} else if(gr.leaves_assignment == "m"){
			// Get the leaves to add every month
			var perMonthM = gr.leaves / 12;

			// Insert the the record in the Leave Bucket table
			var leaveBucketM = new GlideRecord('x_851032_leave_man_leave_bucket');
			leaveBucketM.initialize();
			leaveBucketM.leave_type = gr.leave_type;
			leaveBucketM.accrued = perMonthM;
			leaveBucketM.balance = perMonthM;
			leaveBucketM.taken = 0;
			leaveBucketM.employee = current.sys_id;
			leaveBucketM.insert();

		}
	}

})(current, previous);
